export function useDragDrop({ allowDragDrop, onDrop, orientation, containerRef, itemQuery, }: {
    allowDragDrop: any;
    onDrop: any;
    orientation: any;
    containerRef: any;
    itemQuery?: string | undefined;
}): {
    draggable: null;
    dropIndicator: null;
    draggedItemIndex: any;
    isDragging: boolean;
    onMouseDown: ((evt: any) => void) | undefined;
};
